import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { Batch } from '../models/Batch';
import { InventoryService } from '../_services/Inventory.service';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-edit',
  templateUrl: './edit.component.html',
  styleUrls: ['./edit.component.css']
})
export class EditComponent implements OnInit{
  @Input() batchToEdit: any;
  @Output() cancelEdit = new EventEmitter();
  model: any = {}

  constructor(private  http: HttpClient, private inventoryService: InventoryService) { }

  ngOnInit(): void {
    console.log("batch to edit " + this.batchToEdit);
    this.model = this.batchToEdit;
  }

  edit() {
    console.log(this.model);

    Batch batch = (Batch)model;

    this.http.put('https://localhost:5001/api/batches/' + this.model.id, this.model).subscribe({
      next: response => this.model = response,
      error: error => console.log(error),
      complete: () => console.log(this.model)
  })   

    console.log("edit done");
    this.cancel();
  }

  cancel() {
    this.cancelEdit.emit(false);
    console.log("cancel");
  }
}
<div class="d-flex flex-wrap mx-auto justify-content-center mb-3 ">

    <form #registerForm="ngForm" (ngSubmit)="edit()"> 
        <h2 class="text-center text-primary">Add Batch</h2>
        <hr>
        <div class="form-group" style="margin-top: 10px;">
            <input type="number" class="form-control" name="id" 
                [(ngModel)]="model.id" placeholder="ID"  >
        </div>

    <div class="form-group" style="margin-top: 10px;">
        <input type="text" class="form-control" name="name" 
            [(ngModel)]="model.name" placeholder="Name"  >
    </div>

    <div class="form-group" style="margin-top: 10px;">
        <input type="number" class="form-control" name="quantity" 
            [(ngModel)]="model.quantity" placeholder="Quantity"  >
    </div>

    <div class="form-group" style="margin-top: 10px;">Delivery Date
        <input type="date" class="form-control" name="DeliveryDate" 
            [(ngModel)]="model.deliveryDate" placeholder="Delivery Date"  >
    </div>

    <div class="form-group" style="margin-top: 10px;">Expiration Date
        <input type="date" class="form-control" name="ExpirationDate" 
            [(ngModel)]="model.expirationDate" placeholder="Expiration Date"  >
    </div>


    <div class="form-group text-center" style="margin-top: 20px;">
        <button class="btn btn-success mr-2" type="submit">Edit</button>
        <button class="btn btn-default mr-2" (click)="cancel()" type="button">Cancel</button>
    </div>
</form>
</div>
