import { HttpClient } from '@angular/common/http';
import { Component, OnInit, ViewChild } from '@angular/core';
import { Batch } from './models/Batch';
import { InventoryService } from './_services/Inventory.service';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit{
  batches: any;
  logs: any;
  editBatch: any;
  model: any = {}

  createMode = false;
  historyMode = false;
  batchesMode = false;
  editMode = false  

  constructor(private http: HttpClient, private inventoryService : InventoryService) {}

  ngOnInit() {
  }

  loadBatches() {
    this.http.get('https://localhost:5001/api/batches').subscribe({
      next: response => this.batches = response,
     })
    }

    orderBatches(){
      this.batchesMode = !this.batchesMode;
      if(!this.batchesMode) return;

      this.http.get('https://localhost:5001/api/batches/order').subscribe({
      next: response => this.batches = response,
      })
    }

    search() {
      //this.inventoryService.search(this.model).subscribe;
    }

    openCreate() {
      this.createMode = true;    
    }

    onEditProduct(id: number) {
      console.log("edit " + id);
      this.batchesMode = false;
      this.editMode = true;  

      this.http.get('https://localhost:5001/api/batches/' + id).subscribe({
        next: response => this.editBatch = response,
        error: error => console.log(error),
        complete: () => console.log('Request has completed')
    })      
      console.log(this.editBatch)
    }

    onDeleteProduct(id: number ) {
      this.http.delete('https://localhost:5001/api/batches/delete/' + id).subscribe({
        next: response => this.batches = response,
        error: error => console.log(error),
        complete: () => console.log('Request has completed')
    })
    }

    seeAll() {
      this.batchesMode = !this.batchesMode;
      if(!this.batchesMode) return;

      this.http.get('https://localhost:5001/api/batches').subscribe({
        next: response => this.batches = response,
        error: error => console.log(error),
        complete: () => console.log('Request has completed')
    })
  }

    cancelCreateMode(event: boolean)
    {
      this.createMode = event;
  }
  cancelEditMode(event: boolean)
  {
    this.editMode = event;
  }

    seeHistory() {
      this.historyMode = !this.historyMode;
      if(!this.historyMode) return;

      this.http.get('https://localhost:5001/api/batches/history').subscribe({
        next: response => this.logs = response,
        error: error => console.log(error),
        complete: () => console.log('Request has completed')
    })    
  }
}
<header class="p-3 mb-3 border-bottom">
    <div class="container">
      <div class=" flex-wrap align-items-center justify-content-center justify-content-lg-start">
        <a href="/" class="d-flex align-items-center mb-2 mb-lg-0 link-body-emphasis text-decoration-none">
          <svg class="bi me-2" width="40" height="40" role="img" aria-label="Bootstrap"><use xlink:href="#bootstrap"></use></svg>
        </a>

        <ul class="nav col-12 col-lg-auto me-lg-auto mb-2 justify-content-center mb-md-0">
          <li><a class="nav-link px-4">Inventory Management</a></li>
          <li><a href="#" (click)="seeAll()" class="nav-link px-4 link-body-emphasis">Overview</a></li>
          <li><a href="#" (click)="openCreate()" class="nav-link px-4 link-body-emphasis">Add</a></li>
          <li><a href="#" (click)="orderBatches()" class="nav-link px-4 link-body-emphasis">Freshness</a></li>
          <li><a href="#" (click)="seeHistory()" class="nav-link px-4 link-body-emphasis">History</a></li>
        </ul>
      </div>
    </div>
  </header>

  <router-outlet></router-outlet>

  <div  *ngIf="createMode"> 
    <app-create (cancerlResgister)="cancelCreateMode($event)">
    </app-create>
  </div>


  <div class="container" style="margin-top: 100px;"></div>

  <div *ngIf="batchesMode" class="d-flex">
  <ul class="list-group mx-auto justify-content-center ">
    <li *ngFor="let batch of batches" class="list-group-item float-right "> 
      <div> {{batch.id}} - {{batch.name}} - Quantity: {{batch.quantity}} - Delivery Date: {{batch.deliveryDate}} - Expiration Date: {{batch.expirationDate}}
        <div class="nav col-12 col-lg-auto me-lg-auto justify-content-center mb-2">
        <button type="button"  class="btn btn-primary mx-5"  (click)="onEditProduct(batch.id)">Edit</button><button type="button" class="btn btn-primary mx-5 mb-2" (click)="onDeleteProduct(batch)">Delete</button>
        </div>
      </div>
    </li>
  </ul>
  </div>

  <div *ngIf="editMode"> 
    <app-edit [batchToEdit]="editBatch" (cancelEdit)="cancelEditMode($event)"></app-edit>
  </div>

  <div class="container" style="margin-top: 100px;"></div>

  <div *ngIf="historyMode" class="d-flex">
  <ul class="list-group mx-auto justify-content-center ">
    <li *ngFor="let log of logs" class="list-group-item float-right "> 
      Batch Id: {{log.batchId}} - Change: {{log.change}} - Time: {{log.timeStamp}}
    </li>
  </ul>
  </div>
