import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, ReplaySubject, map } from 'rxjs';
import { Batch } from '../models/Batch';

@Injectable({
  providedIn: 'root'
})
export class InventoryService {
  baseUrl = 'https://localhost:5001/api/'
  private currentBatchSource = new ReplaySubject<Batch>(1);
  currentBatches = this.currentBatchSource.asObservable();
  newBatch = new Batch();

  constructor(private http: HttpClient) { }

  public getAllBatches() : Observable<Batch[]> {
    return this.http.get<Batch[]>(this.baseUrl + 'branches/');
  }

  create(model: any)
  {
    console.log(this.currentBatches);

    localStorage.setItem('batch', JSON.stringify(this.newBatch));
    this.currentBatchSource.next(this.newBatch);

    console.log("reached this far");

    return this.http.post<Batch>(this.baseUrl +'create', model).pipe(
      map((newBatch: Batch) => {
        if (newBatch) {
          console.log("reached far");
          localStorage.setItem('batch', JSON.stringify(newBatch));
          this.currentBatchSource.next(newBatch);
        }
        else
        console.log("reached far");
      })
    )
  }

  search(model: any)
  {
    return this.http.get(this.baseUrl + 'branches/', model);
  }
}
